from unittest import TestCase

def clean_matrix_relation_dfs(row_index, matrix, len_matrix):
    if matrix[row_index][row_index] == 0:
        return
    else: matrix[row_index][row_index] = 0

    for column_i in range(len_matrix):
        if matrix[row_index][column_i] == 1:
            matrix[row_index][column_i] = 0
            clean_matrix_relation_dfs(column_i, matrix, len_matrix)

def convert_string_matrix_to_int(matrix):
    convert_tmp = []

    for row in matrix:
        convert_tmp.append([int(x) for x in row])
    return convert_tmp


def count_groups(related):

    relation_count = 0
    
    len_matrix = len(related)
    related = convert_string_matrix_to_int(related)


    for row_idx, row in enumerate(related, 0):

        if row[row_idx] == 1:
            relation_count += 1

            clean_matrix_relation_dfs(row_idx, related, len_matrix)

    return relation_count


